const µ = require('.')
const base64 = require('./b64')

const die = message => {
    console.error(message)
    process.exit()
}

if(process.argv.length < 3) die('use: sync.js [url] <options>')
let [url, options] = process.argv.slice(2)
options = JSON.parse(base64.decode(options))

µ(url, options).then($ => console.log($.html())).catch(console.error)